<?php	include_once( CLASS_PATH . '/catalog/GraphicsDentition.php' );	include_once( CLASS_PATH . '/catalog/AreaTooth.php' );					$list = new GraphicsDentition();		$result['list'] = $list->getAll_GraphicsDentition();					$listAreaTooth = new AreaTooth();		$result['listAreaTooth'] = $listAreaTooth->getAll_AreaTooth();				$idGraph = isset($_GET['idGraph']) ? $_GET['idGraph'] : null;			if($idGraph){				$getGraph = new GraphicsDentition();				$result['idGraph'] = $getGraph->get_GraphicsDentition($idGraph);			}				$sAct = isset($_GET['sAct']) ? $_GET['sAct'] : "";		switch($sAct) {			case 'new':					$result['idGraph'] = null;					newObject($result);				break;			case 'insert':					insert($result);				break;			case 'update':					update();				break;			default:				def($result);		}		function update(){						$update = new GraphicsDentition();				$update->set_GraphicsDentition($_GET);		$res =	$update->update_GraphicsDentition();				header( "Location: admin.php?action=catalog&cat=graphicsDentition&idGraph=".$_GET['idGraph']."&res=".$res );	}		function def($result){		include ( TEMPLATE_PATH . '/catalog/graphicsDentition.php' );	}		function newObject($result){		include ( TEMPLATE_PATH . '/catalog/graphicsDentition.php' );	}		function insert($result){		$insert = new GraphicsDentition();		$insert->set_GraphicsDentition($_GET);		$res = $insert->insert_GraphicsDentition();							header( "Location: admin.php?action=catalog&cat=graphicsDentition&res=".$res );	}		// Graphics Dentition?>