<?php	/******************************************************************************/	/***						Print043 function				                ***/	/***				Обозначение для печати карточки Ф-043					***/	/******************************************************************************/	if(!$result['cat']){return;}?><?php?><script LANGUAGE="JavaScript"><!--function confirmSubmit(){var agree=confirm(	'Удаление данного элемента может привести к нарушению отчётности!\n\n'	+'\tВы действительно желаете удалить?');if (agree)	return true ;else	return false ;}// --></script><table border="1" width="100%"><thead>	Обозначение для печати карточки Ф-043	<?php 		if(isset($result['res'])){			if($result['res']=='success'){ echo" | Операция выполнена успешно.";}			else{echo" | " . $result['res'];}		}	?></thead><tbody>	<tr>		<td width="30%">			<p style="height: 30px;">			<table class="list">				<tr>					<th>Название</th>					<th>Описание</th>				</tr>				<?php				// Левая панель перечисления				if(!$result['list']){ echo "<tr><td>Обозначений нет</td></tr>";}				else{					foreach( $result['list'] as $value ){						if($value['id']==$result['GETid']){							echo "<tr class='active' onclick=\"location='".$result['link']."&id=".$value['id']."'\"> ";						}else{							echo "<tr onclick=\"location='".$result['link']."&id=".$value['id']."'\"> ";						}						echo "<td>".$value['name']."</td>";						echo "<td>".$value['description']."</td>";						echo "</tr>";					}				}				?>			</table>			</p>			</td>		<td width="70%">						<?php				$res['link'] = $result['link'];				if(isset($result['GETid']) || isset($result['sAct'])){										$res['sAct'] = $result['sAct'];					$res['id'] = isset($result['id']) ?  $result['id'] : null;					$res['cat'] = $result['cat'];										if($res['id']){						activeForm($res);					}else{												newForm($res);					}									}				else{ topform($res); }			?>					</td>	</tr></tbody></table><?php	function topform($result){		echo '			<div class="actions button-container button-group">				<a class="button big" href="'.$result['link'].'&sAct=new">Новое обозначение</a>			</div>			<br><hr><br>		';	}	function formButton(){		echo '			<div class="actions button-container">				<a href="'.$result['link'].'&sAct=new" class="button primary icon add">Новое обозначение</a>    				<div class="button-group">					<input form="form" type="submit" value="Сохранить" class="button primary" />					<input form="form" type="reset" value="Отменить" class="button" />					<input form="form" type="reset" value="Удалить" class="button danger" />				</div>                        				<div class="button-group minor-group">					<a href="'.$result['link'].'" class="button">К списку</a>					<a href="./admin.php" class="button">Выйти</a>									</div>			</div>		';		}	function newForm($result){		echo '<form id="form" method="post" action="'.$result['link'].'&sAct=insert" >';		echo '<input type="hidden" formmethod="get" name="action" value="catalog" />';		echo '<input type="hidden" formmethod="get" name="cat" value="print043" />';				echo '			<div class="actions button-container">				<a href="'.$result['link'].'&sAct=new" class="button primary icon add">Новое обозначение</a>    				<div class="button-group">					<input form="form" type="submit" value="Сохранить" class="button primary" />					<input form="form" type="reset" value="Отменить" class="button" />				</div>                        				<div class="button-group minor-group">					<a href="'.$result['link'].'" class="button">К списку</a>					<a href="./admin.php" class="button">Выйти</a>									</div>			</div>		';	echo '</form>';	echo '<hr>';		echo 'Название*:';			echo '<input name="name" form="form" type="text" size="16" required /><br/>';		echo 'Описание*:';			echo '<input name="description" form="form" type="text" size="64" required /><br/>';	}		function activeForm($result){		// Кнопки управления		echo '<form id="form" method="post" action="'.$result['link'].'&id='. $result['id']['id'] .'&sAct=update">';						echo '			<div class="actions button-container">				<a href="'.$result['link'].'&sAct=new" class="button primary icon add">Новое обозначение</a>    				<div class="button-group">					<input form="form" type="submit" value="Сохранить изменения" class="button primary" />					<input form="form" type="reset" value="Отменить" class="button" />					<a href="?action=catalog&cat='.$result['cat'].'&id='. $result['id']['id'] .'&sAct=delete" onclick="return confirmSubmit()" class="button danger">Удалить</a>				</div>                        				<div class="button-group minor-group">					<a href="'.$result['link'].'" class="button">К списку</a>					<a href="./admin.php" class="button">Выйти</a>									</div>			</div>		';					echo '</form>';		echo '<hr>';		// Рабочая часть		echo '<input type="hidden" formmethod="get" name="id" value="'.$result['id']['id'].'" form="form" />';		echo 'Название*:';			echo '<input name="name" form="form" type="text" size="16" value="'.$result['id']['name'].'" required /><br/>';		echo 'Описание*:';			echo '<input name="description" form="form" type="text" size="64" value="'.$result['id']['description'].'" required /><br/>';	}?>